import UIKit

class ViewController: UIViewController {

    @IBOutlet weak var textLabel: UILabel!
    
    
    @IBAction func buttonPressed(_ sender: UIButton) {
    
    let quiz = [
        "рЃДрЃЋрЃћрЃџрЃљ рЃЏрЃЮрЃЌрЃљрЃЏрЃљрЃерЃћ *FACEBOOK* рЃърЃарЃЮрЃцрЃўрЃџрЃќрЃћ рЃљрЃЦрЃЋрЃћрЃДрЃюрЃћрЃЉрЃА рЃўрЃЏ рЃцрЃЮрЃбрЃЮрЃА, рЃарЃЮрЃЏрЃћрЃџрЃАрЃљрЃф рЃЏрЃўрЃАрЃњрЃљрЃю рЃЏрЃљрЃарЃ»рЃЋрЃюрЃўрЃЋ рЃЏрЃ»рЃЊрЃЮрЃЏрЃў рЃљрЃБрЃарЃЕрЃћрЃЋрЃА рЃЏрЃўрЃАрЃўрЃЋрЃћ рЃбрЃћрЃџрЃћрЃцрЃЮрЃюрЃўрЃЊрЃљрЃю.рЃцрЃЮрЃбрЃЮ рЃарЃЕрЃћрЃЉрЃљ 30 рЃгрЃБрЃЌрЃўрЃЌ.рЃЋрЃўрЃюрЃф рЃБрЃљрЃарЃА рЃўрЃбрЃДрЃЋрЃўрЃА, рЃАрЃЋрЃљрЃЏрЃА рЃАрЃљрЃЏрЃ»рЃћрЃа.",
        "рЃњрЃљрЃБрЃњрЃќрЃљрЃЋрЃюрЃћ рЃЏрЃћрЃњрЃЮрЃЉрЃљрЃарЃА рЃўрЃюрЃбрЃћрЃарЃюрЃћрЃбрЃерЃў рЃЏрЃЮрЃФрЃўрЃћрЃЉрЃБрЃџрЃў рЃърЃћрЃю@рЃАрЃўрЃА рЃцрЃЮрЃбрЃЮ рЃЊрЃљ рЃљрЃа рЃБрЃърЃљрЃАрЃБрЃ«рЃЮ рЃ«рЃЋрЃљрЃџрЃљрЃЏрЃЊрЃћ. рЃгрЃўрЃюрЃљрЃљрЃдрЃЏрЃЊрЃћрЃњ рЃерЃћрЃЏрЃЌрЃ«рЃЋрЃћрЃЋрЃљрЃерЃў рЃАрЃЋрЃљрЃЏ рЃ«рЃБрЃЌрЃ»рЃћрЃа.",
        "рЃљрЃўрЃарЃЕрЃўрЃћ рЃЏрЃЮрЃЌрЃљрЃЏрЃљрЃерЃћ. 1 рЃгрЃБрЃЌрЃўрЃА рЃњрЃљрЃюрЃЏрЃљрЃЋрЃџрЃЮрЃЉрЃљрЃерЃў рЃЌрЃЋрЃљрЃџрЃћрЃЉрЃўрЃА рЃЊрЃљрЃБрЃ«рЃљрЃЏрЃ«рЃљрЃЏрЃћрЃЉрЃџрЃљрЃЊ рЃБрЃДрЃБрЃарЃћрЃЌ рЃћрЃарЃЌрЃЏрЃљрЃюрЃћрЃЌрЃА. рЃЋрЃўрЃюрЃф рЃърЃўрЃарЃЋрЃћрЃџрЃў рЃЊрЃљрЃљрЃ«рЃљрЃЏрЃ«рЃљрЃЏрЃћрЃЉрЃА рЃАрЃЋрЃљрЃЏрЃА рЃЮрЃарЃ»рЃћрЃа.",
        "рЃљрЃдрЃўрЃљрЃарЃћ, рЃарЃЮрЃЏрЃћрЃџрЃў рЃЏрЃЮрЃЌрЃљрЃЏрЃљрЃерЃўрЃА рЃљрЃ«рЃџрЃЮрЃЉрЃћрЃџрЃў(рЃюрЃљрЃЌрЃћрЃАрЃљрЃЋрЃў) рЃљрЃарЃўрЃА рЃДрЃЋрЃћрЃџрЃљрЃќрЃћ рЃАрЃћрЃЦрЃАрЃБрЃљрЃџрЃБрЃарЃў. рЃћрЃА рЃЏрЃЮрЃЌрЃљрЃЏрЃљрЃерЃћ рЃАрЃЋрЃљрЃЏрЃА. рЃгрЃўрЃюрЃљрЃљрЃдрЃЏрЃЊрЃћрЃњ рЃерЃћрЃЏрЃЌрЃ«рЃЋрЃћрЃЋрЃљрЃерЃў рЃАрЃљрЃЏрЃ»рЃћрЃа рЃАрЃЋрЃљрЃЏ рЃерЃћрЃю.",
        "рЃДрЃЋрЃћрЃџрЃљ рЃерЃћрЃДрЃЋрЃљрЃарЃћрЃЉрЃБрЃџрЃў рЃгрЃДрЃЋрЃўрЃџрЃў рЃАрЃЋрЃљрЃЏрЃА рЃЋрЃљрЃ«рЃбрЃљрЃюрЃњрЃБрЃарЃљрЃЊ. рЃЌрЃБ рЃЌрЃЦрЃЋрЃћрЃюрЃА рЃерЃЮрЃарЃўрЃА рЃгрЃДрЃЋрЃўрЃџрЃў рЃљрЃа рЃљрЃарЃўрЃА рЃАрЃЋрЃљрЃЏрЃЌ рЃДрЃЋрЃћрЃџрЃљ.",
        "рЃЋрЃўрЃАрЃљрЃф рЃАрЃљрЃќрЃЮрЃњрЃљрЃЊрЃЮрЃћрЃЉрЃарЃўрЃЋ рЃбрЃарЃљрЃюрЃАрЃърЃЮрЃарЃбрЃерЃў рЃАрЃ«рЃЋрЃўрЃАрЃў рЃЏрЃћрЃАрЃўрЃ»рЃћрЃЉрЃў рЃгрЃљрЃБрЃЎрЃўрЃЌрЃ«рЃљрЃЋрЃА, рЃАрЃЋрЃљрЃЏрЃА.",
        "рЃљрЃўрЃдрЃћ рЃбрЃћрЃџрЃћрЃцрЃЮрЃюрЃў рЃЊрЃљ рЃфрЃ«рЃЋрЃўрЃарЃерЃў рЃњрЃљрЃљрЃгрЃљрЃюрЃў рЃўрЃЏ рЃЏрЃЮрЃЌрЃљрЃЏрЃљрЃерЃћрЃА, рЃарЃЮрЃЏрЃћрЃџрЃўрЃф рЃЊрЃљрЃџрЃћрЃЋрЃљрЃќрЃћ рЃЌрЃљрЃЎрЃўрЃџрЃЮрЃЉрЃА. рЃЌрЃБ рЃБрЃљрЃарЃА рЃњрЃћрЃбрЃДрЃЋрЃўрЃА, рЃАрЃЋрЃљрЃЏрЃА рЃАрЃљрЃЏрЃ»рЃћрЃа.",
        "рЃЊрЃљрЃўрЃГрЃўрЃарЃћ рЃћрЃюрЃљ рЃЮрЃарЃў рЃЌрЃўрЃЌрЃўрЃЌ рЃЊрЃљ рЃўрЃЏрЃдрЃћрЃарЃћ *рЃЌрЃЉрЃўрЃџрЃўрЃАрЃЮ*.рЃгрЃўрЃюрЃљрЃљрЃдрЃЏрЃЊрЃћрЃњ рЃерЃћрЃЏрЃЌрЃ«рЃЋрЃћрЃЋрЃљрЃерЃў рЃАрЃЋрЃљрЃЏ рЃАрЃљрЃЏрЃ»рЃћрЃа.",
        "рЃЏрЃЮрЃЌрЃљрЃЏрЃљрЃерЃћрЃћрЃЉрЃў рЃўрЃарЃЕрЃћрЃЋрЃћрЃю, рЃЌрЃБ рЃарЃЮрЃЏрЃћрЃџ рЃюрЃўрЃЋрЃЌрЃЌрЃљрЃю рЃњрЃљрЃЊрЃљрЃўрЃдрЃЮ рЃАрЃћрЃџрЃцрЃў рЃЊрЃљ рЃњрЃљрЃЏрЃЮрЃљрЃЦрЃЋрЃћрЃДрЃюрЃЮ рЃАрЃЮрЃфрЃўрЃљрЃџрЃБрЃа рЃЦрЃАрЃћрЃџрЃерЃў. рЃцрЃЮрЃбрЃЮ рЃарЃЕрЃћрЃЉрЃљ 30 рЃгрЃБрЃЌрЃўрЃЌ. рЃБрЃљрЃарЃўрЃА рЃерЃћрЃЏрЃЌрЃ«рЃЋрЃћрЃЋрЃљрЃерЃў рЃАрЃЋрЃљрЃЏ рЃАрЃљрЃЏрЃ»рЃћрЃа.",
        "*рЃерЃћрЃЏрЃЮрЃАрЃгрЃарЃћрЃЉрЃБрЃџрЃА рЃњрЃљрЃБрЃЏрЃљрЃарЃ»рЃЮрЃА!!!!* рЃЋрЃўрЃюрЃф рЃЉрЃЮрЃџрЃЮрЃА рЃерЃћрЃЏрЃЮрЃњрЃўрЃћрЃарЃЌрЃЊрЃљрЃЌ рЃАрЃЋрЃљрЃЏрЃА рЃЮрЃарЃ»рЃћрЃа.",
        "рЃерЃћрЃюрЃА рЃерЃћрЃЏрЃЊрЃћрЃњ рЃАрЃЋрЃџрЃљрЃЏрЃЊрЃћ рЃДрЃЋрЃћрЃџрЃљрЃА, рЃЋрЃўрЃюрЃф рЃЊрЃљрЃњрЃћрЃџрЃљрЃърЃљрЃарЃљрЃЎрЃћрЃЉрЃљ, рЃБрЃърЃљрЃАрЃБрЃ«рЃћ рЃДрЃћрЃцрЃўрЃЌ. рЃЌрЃБ рЃарЃљрЃЏрЃћ рЃАрЃўрЃбрЃДрЃЋрЃљ рЃгрЃљрЃЏрЃЮрЃњрЃфрЃЊрЃћрЃЉрЃљ, рЃАрЃЋрЃљрЃЏ рЃЮрЃЌрЃ«рЃ»рЃћрЃа.",
        "рЃЊрЃљрЃБрЃарЃћрЃЎрЃћ рЃДрЃЮрЃцрЃўрЃџрЃА рЃАрЃърЃўрЃЎрЃћрЃарЃќрЃћ рЃЊрЃљ рЃБрЃЌрЃ«рЃљрЃарЃў, рЃарЃЮрЃЏ рЃЊрЃљрЃЉрЃџрЃљ рЃ«рЃљрЃа рЃЊрЃљ рЃерЃћрЃарЃўрЃњрЃћрЃЉрЃљ рЃњрЃўрЃюрЃЊрЃљ.рЃгрЃўрЃюрЃљрЃљрЃдрЃЏрЃЊрЃћрЃњ рЃерЃћрЃЏрЃЌрЃ«рЃЋрЃћрЃЋрЃљрЃерЃў рЃАрЃЋрЃљрЃЏ рЃЮрЃЌрЃ«рЃ»рЃћрЃа.",
        "рЃДрЃЋрЃћрЃџрЃљ рЃЊрЃљрЃЊрЃћрЃЦрЃўрЃЌ рЃфрЃљрЃџ рЃцрЃћрЃ«рЃќрЃћ рЃўрЃАрЃћ, рЃарЃЮрЃЏ рЃљрЃарЃљрЃЋрЃўрЃА рЃЊрЃљ рЃљрЃарЃљрЃцрЃћрЃарЃА рЃЏрЃўрЃћрЃДрЃарЃЊрЃюрЃЮрЃЌ рЃърЃўрЃарЃЋрЃћрЃџрЃў рЃАрЃљрЃЏрЃў рЃљрЃЊрЃљрЃЏрЃўрЃљрЃюрЃў, рЃЋрЃўрЃюрЃф рЃЋрЃћрЃа рЃњрЃљрЃФрЃџрЃћрЃЉрЃА, рЃАрЃЋрЃљрЃЏрЃА рЃЮрЃарЃ»рЃћрЃа.",
        "рЃЊрЃљрЃБрЃарЃћрЃЎрЃћ рЃарЃЮрЃЏрЃћрЃџрЃўрЃЏрЃћ рЃЏрЃћрЃњрЃЮрЃЉрЃљрЃарЃА рЃЊрЃљ рЃАрЃЌрЃ«рЃЮрЃЋрЃћ рЃњрЃљрЃЊрЃЏрЃЮрЃњрЃўрЃарЃўрЃфрЃ«рЃЮрЃА 100 рЃџрЃљрЃарЃў. рЃЌрЃБ рЃњрЃљрЃЊрЃЏрЃЮрЃњрЃўрЃарЃўрЃфрЃ«рЃљрЃЋрЃА рЃАрЃЋрЃљрЃЏрЃА рЃДрЃЋрЃћрЃџрЃљ, рЃЌрЃБ рЃБрЃљрЃарЃА рЃњрЃћрЃбрЃДрЃЋрЃўрЃА, рЃЮрЃарЃ»рЃћрЃа рЃАрЃЋрЃљрЃЏ рЃерЃћрЃю.",
        "рЃњрЃљрЃљрЃЎрЃћрЃЌрЃћрЃЉрЃўрЃюрЃћ рЃЏрЃљрЃАрЃљрЃЪрЃў рЃарЃЮрЃЏрЃћрЃџрЃўрЃЏрЃћ рЃЏрЃЮрЃЌрЃљрЃЏрЃљрЃерЃћрЃА. рЃЌрЃБ рЃЊрЃљрЃњрЃЌрЃљрЃюрЃ«рЃЏрЃЊрЃћрЃЉрЃљ, рЃАрЃЋрЃљрЃЏ рЃерЃћрЃю. рЃЌрЃБ рЃБрЃљрЃарЃА рЃњрЃћрЃбрЃДрЃЋрЃўрЃА, рЃАрЃЋрЃљрЃЏрЃА рЃАрЃљрЃЏрЃ»рЃћрЃа.",
        "рЃљрЃўрЃарЃЕрЃўрЃћ рЃЋрЃўрЃюрЃЏрЃћ, рЃћрЃЌрЃљрЃЏрЃљрЃерЃћ рЃ»рЃћрЃўрЃарЃљрЃюрЃў рЃАрЃљрЃЏрЃљрЃЏрЃЊрЃћ, рЃгрЃљрЃњрЃћрЃЉрЃБрЃџрЃў рЃАрЃЋрЃљрЃЏрЃА рЃўрЃАрЃћ, рЃарЃЮрЃЏ рЃАрЃљрЃАрЃЏрЃўрЃАрЃА рЃърЃўрЃарЃА рЃљрЃа рЃљрЃЎрЃљрЃарЃћрЃЉрЃА.",
        "рЃћрЃарЃЌрЃЏрЃљрЃюрЃћрЃЌрЃА рЃЋрЃљрЃЕрЃЋрЃћрЃюрЃЮрЃЌ ID рЃЉрЃљрЃарЃљрЃЌрЃўрЃА рЃцрЃЮрЃбрЃЮрЃћрЃЉрЃў. рЃЋрЃўрЃАрЃљрЃф рЃДрЃЋрЃћрЃџрЃљрЃќрЃћ рЃАрЃљрЃАрЃљрЃфрЃўрЃџрЃЮ рЃцрЃЮрЃбрЃЮ рЃљрЃЦрЃЋрЃА рЃАрЃЋрЃљрЃЏрЃА рЃЮрЃарЃ»рЃћрЃа. рЃЋрЃўрЃюрЃф рЃљрЃа рЃњрЃљрЃЕрЃЋрЃћрЃюрЃћрЃЉрЃЌ рЃАрЃЋрЃљрЃЏрЃА рЃЮрЃЌрЃ«рЃ»рЃћрЃа.",
        "рЃЏрЃЮрЃДрЃћрЃЋрЃў рЃљрЃюрЃћрЃЎрЃЊрЃЮрЃбрЃў. рЃЋрЃўрЃюрЃф рЃњрЃљрЃўрЃфрЃўрЃюрЃћрЃЉрЃА рЃАрЃЋрЃљрЃЏрЃА рЃЮрЃарЃ»рЃћрЃа. рЃЌрЃБ рЃљрЃарЃљрЃЋрЃўрЃА рЃњрЃљрЃћрЃфрЃўрЃюрЃћрЃЉрЃљ, рЃЮрЃарЃ»рЃћрЃа рЃАрЃЋрЃљрЃЏ рЃерЃћрЃю.",
        "рЃЋрЃўрЃюрЃф рЃЊрЃљрЃЮрЃ»рЃљрЃ«рЃћрЃЉрЃБрЃџрЃўрЃљ, рЃАрЃЋрЃљрЃЏрЃА.",
        "рЃњрЃљрЃюрЃљрЃАрЃљрЃ«рЃўрЃћрЃарЃћ рЃАрЃћрЃЦрЃАрЃўрЃА 6 рЃърЃЮрЃќрЃљ 10 рЃгрЃљрЃЏрЃўрЃА рЃњрЃљрЃюрЃЏрЃљрЃЋрЃџрЃЮрЃЉрЃљрЃерЃў. рЃАрЃЋрЃљрЃЏ рЃўрЃЏрЃЊрЃћрЃюрЃ»рЃћрЃа, рЃарЃљрЃЏрЃЊрЃћрЃюрЃў рЃърЃЮрЃќрЃўрЃА рЃњрЃљрЃЎрЃћрЃЌрЃћрЃЉрЃљрЃАрЃљрЃф рЃЋрЃћрЃа рЃЏрЃЮрЃљрЃАрЃгрЃарЃћрЃЉ. рЃЌрЃБ рЃЊрЃарЃЮрЃерЃў рЃЕрЃљрЃћрЃбрЃћрЃЋрЃў рЃАрЃЋрЃљрЃЏрЃА рЃДрЃЋрЃћрЃџрЃљ.",
        "рЃАрЃљрЃЏ рЃЌрЃЋрЃџрЃљрЃќрЃћ рЃДрЃЋрЃћрЃџрЃљрЃюрЃў рЃўрЃерЃЋрЃћрЃарЃЌ рЃ«рЃћрЃџрЃА рЃўрЃЏ рЃљрЃЊрЃљрЃЏрЃўрЃљрЃюрЃўрЃАрЃЎрЃћрЃю, рЃарЃЮрЃЏрЃћрЃџрЃЏрЃљрЃф, рЃЌрЃЦрЃЋрЃћрЃюрЃў рЃљрЃќрЃарЃўрЃЌ рЃДрЃЋрЃћрЃџрЃљрЃќрЃћ рЃЉрЃЮрЃџрЃЮрЃА рЃБрЃДрЃБрЃарЃљ рЃърЃЮрЃарЃюрЃЮрЃА. рЃћрЃА рЃљрЃЊрЃљрЃЏрЃўрЃљрЃюрЃў рЃАрЃЋрЃљрЃЏрЃА рЃўрЃЏрЃЊрЃћрЃюрЃ»рЃћрЃа, рЃарЃљрЃЏрЃЊрЃћрЃюрЃўрЃф рЃЏрЃўрЃАрЃЎрЃћрЃю рЃњрЃљрЃўрЃерЃЋрЃћрЃарЃА рЃ«рЃћрЃџрЃА.",
        "рЃЏрЃЮрЃДрЃћрЃЋрЃў рЃерЃћрЃюрЃў рЃърЃўрЃарЃЋрЃћрЃџрЃў рЃЎрЃЮрЃфрЃюрЃўрЃА рЃўрЃАрЃбрЃЮрЃарЃўрЃљ рЃљрЃю рЃЊрЃљрЃџрЃўрЃћ рЃЮрЃарЃ»рЃћрЃа. рЃЌрЃБ рЃљрЃарЃљрЃЋрЃўрЃАрЃЌрЃЋрЃўрЃА рЃњрЃўрЃЎрЃЮрЃфрЃюрЃўрЃљ, рЃАрЃЋрЃљрЃЏ рЃАрЃљрЃЏрЃ»рЃћрЃа.",
        "рЃдрЃарЃЏрЃљрЃЊ рЃЕрЃљрЃўрЃАрЃБрЃюрЃЌрЃЦрЃћ рЃЊрЃљ рЃерЃћрЃўрЃЎрЃљрЃЋрЃћ рЃАрЃБрЃюрЃЌрЃЦрЃЋрЃљ 40 рЃгрЃљрЃЏрЃўрЃЌ. рЃърЃўрЃарЃЋрЃћрЃџрЃў рЃАрЃљрЃЏрЃў рЃЏрЃЮрЃЌрЃљрЃЏрЃљрЃерЃћ, рЃЋрЃўрЃюрЃф рЃЊрЃљрЃюрЃћрЃЉрЃЊрЃћрЃЉрЃљ, рЃАрЃЋрЃљрЃЏрЃА рЃЮрЃарЃ»рЃћрЃа.",
        "рЃЋрЃўрЃАрЃљрЃф рЃљрЃЏ рЃЮрЃЌрЃљрЃ«рЃерЃў рЃЏрЃДрЃЮрЃцрЃЌрЃљрЃњрЃљрЃю рЃЋрЃўрЃюрЃЏрЃћрЃАрЃЌрЃЋрЃўрЃА рЃбрЃБрЃЕрЃћрЃЉрЃерЃў рЃњрЃўрЃЎрЃЮрЃфрЃюрЃўрЃљрЃЌ, рЃАрЃЋрЃљрЃЏрЃЌ. рЃЌрЃБ рЃљрЃАрЃћрЃЌрЃў рЃљрЃарЃљрЃЋрЃўрЃюрЃљрЃљ рЃДрЃЋрЃћрЃџрЃљ рЃАрЃЋрЃљрЃЏрЃЌ.",
        "рЃЏрЃўрЃгрЃћрЃарЃћ рЃЊрЃћрЃЊрЃљрЃерЃћрЃюрЃА рЃљрЃю рЃЏрЃљрЃЏрЃљрЃерЃћрЃюрЃА: рЃЏрЃЮрЃАрЃљрЃгрЃћрЃЋрЃЏрЃљ рЃЏрЃљрЃњрЃљрЃарЃў *рЃърЃарЃўрЃ«рЃЮрЃЊрЃў* рЃЏрЃЮрЃЏрЃфрЃљ.рЃгрЃўрЃюрЃљрЃљрЃдрЃЏрЃЊрЃћрЃњ рЃерЃћрЃЏрЃЌрЃ«рЃЋрЃћрЃЋрЃљрЃерЃў рЃАрЃЋрЃљрЃЏ рЃАрЃљрЃЏрЃ»рЃћрЃа.",
        "рЃЊрЃљрЃџрЃўрЃћ рЃЊрЃљ рЃЏрЃЮрЃўрЃцрЃўрЃЦрЃарЃћ рЃарЃљрЃўрЃЏрЃћ рЃгрЃћрЃАрЃў, рЃарЃЮрЃЏрЃћрЃџрЃАрЃљрЃф рЃДрЃЋрЃћрЃџрЃљ рЃЏрЃЮрЃЌрЃљрЃЏрЃљрЃерЃћ рЃБрЃюрЃЊрЃљ рЃЊрЃљрЃћрЃЏрЃЮрЃарЃЕрЃўрЃџрЃЮрЃА рЃерЃћрЃюрЃА рЃерЃћрЃЏрЃЊрЃћрЃњ рЃАрЃЋрЃџрЃљрЃЏрЃЊрЃћ. рЃАрЃЋрЃљрЃЏрЃА рЃўрЃА рЃЋрЃўрЃюрЃф рЃљрЃЏ рЃгрЃћрЃАрЃА рЃЊрЃљрЃљрЃарЃдрЃЋрЃћрЃЋрЃА.",
        "рЃЕрЃљрЃарЃЌрЃћ рЃџрЃљрЃўрЃЋрЃў рЃерЃћрЃюрЃА рЃерЃћрЃЏрЃЊрЃћрЃњ рЃАрЃЋрЃџрЃљрЃЏрЃЊрЃћ рЃљрЃю рЃЊрЃљрЃџрЃўрЃћ рЃЮрЃарЃ»рЃћрЃа.",
        "рЃЊрЃљрЃўрЃгрЃДрЃћ рЃарЃЮрЃЏрЃћрЃџрЃўрЃЏрЃћ рЃфрЃюрЃЮрЃЉрЃўрЃџрЃў рЃАрЃўрЃЏрЃдрЃћрЃарЃљ. рЃерЃћрЃюрЃА рЃерЃћрЃЏрЃЊрЃћрЃњ, рЃДрЃЮрЃЋрЃћрЃџрЃў рЃЏрЃЮрЃЏрЃЊрЃћрЃЋрЃюрЃЮ рЃљрЃњрЃарЃФрЃћрЃџрЃћрЃЉрЃА рЃЏрЃљрЃА. рЃЋрЃўрЃАрЃљрЃф рЃерЃћрЃћрЃерЃџрЃћрЃЉрЃљ, рЃАрЃЋрЃљрЃЏрЃА.",
        "рЃЊрЃљрЃљрЃ«рЃљрЃбрЃћ рЃДрЃЋрЃћрЃџрЃљ рЃЏрЃЮрЃЌрЃљрЃЏрЃљрЃерЃћрЃА рЃЎрЃћрЃцрЃљрЃќрЃћ *­ЊѓИ*, рЃарЃљрЃф рЃЊрЃљрЃарЃЕрЃћрЃЉрЃљ рЃЌрЃљрЃЏрЃљрЃерЃўрЃА рЃЉрЃЮрЃџрЃЮрЃЏрЃЊрЃћ.рЃБрЃљрЃарЃўрЃА рЃерЃћрЃЏрЃЌрЃ«рЃЋрЃћрЃЋрЃљрЃерЃў, рЃАрЃЋрЃљрЃЏрЃА рЃЮрЃарЃ»рЃћрЃа.",
        "рЃДрЃЋрЃћрЃџрЃљ рЃЦрЃљрЃџрЃўрЃерЃЋрЃўрЃџрЃў/рЃЋрЃљрЃЪрЃўрЃерЃЋрЃўрЃџрЃў рЃАрЃЋрЃљрЃЏрЃА рЃЮрЃарЃ»рЃћрЃа.",
        "рЃерЃћрЃюрЃњрЃљрЃю рЃЏрЃљрЃарЃ»рЃЋрЃюрЃўрЃЋ рЃЏрЃ»рЃЊрЃЮрЃЏрЃў рЃАрЃЋрЃљрЃЋрЃА рЃЮрЃарЃ»рЃћрЃа.",
        "рЃЕрЃљрЃЏрЃЮрЃЌрЃЋрЃљрЃџрЃћ рЃДрЃЋрЃћрЃџрЃљ рЃЏрЃЌрЃљрЃЏрЃљрЃерЃўрЃА рЃАрЃљрЃ«рЃћрЃџрЃў рЃЊрЃљ рЃњрЃЋрЃљрЃарЃў. рЃДрЃЋрЃћрЃџрЃљ рЃерЃћрЃфрЃЊрЃЮрЃЏрЃљрЃќрЃћ рЃАрЃЋрЃљрЃЏ. рЃЌрЃБ рЃљрЃа рЃерЃћрЃњрЃћрЃерЃџрЃћрЃЉрЃљ рЃАрЃЋрЃљрЃЏрЃА рЃДрЃЋрЃћрЃџрЃљ.",
        "рЃгрЃљрЃЏрЃўрЃЌ рЃерЃћрЃБрЃдрЃўрЃбрЃўрЃюрЃћ рЃДрЃЋрЃћрЃџрЃљрЃА. рЃЋрЃўрЃюрЃф рЃњрЃљрЃўрЃфрЃўрЃюрЃћрЃЉрЃА рЃАрЃЋрЃљрЃЏрЃА рЃЮрЃарЃ»рЃћрЃа. рЃЌрЃБ рЃљрЃарЃљрЃЋрЃўрЃю рЃњрЃљрЃўрЃфрЃўрЃюрЃћрЃЉрЃА, рЃЮрЃарЃ»рЃћрЃа рЃАрЃЋрЃљрЃЏ рЃерЃћрЃю.",
        "рЃЋрЃўрЃюрЃф *рЃњрЃљрЃърЃарЃљрЃюрЃГрЃБрЃџрЃў* рЃЏрЃЮрЃЋрЃўрЃЊрЃљ, рЃАрЃЋрЃљрЃЏрЃА.",
        "рЃљрЃўрЃарЃЕрЃўрЃћ рЃарЃЮрЃЏрЃћрЃџрЃўрЃЏрЃћ рЃЏрЃЮрЃЌрЃљрЃЏрЃљрЃерЃћ,. рЃњрЃљрЃ«рЃАрЃћрЃюрЃў рЃЏрЃўрЃАрЃў рЃўрЃюрЃАрЃбрЃљрЃњрЃарЃљрЃЏрЃўрЃЊрЃљрЃю рЃарЃЮрЃЏрЃћрЃџрЃўрЃЏрЃћ рЃърЃарЃЮрЃцрЃўрЃџрЃў рЃЊрЃљ рЃЊрЃљрЃљрЃџрЃљрЃўрЃЦрЃћ 10 рЃДрЃЋрЃћрЃџрЃљрЃќрЃћ рЃФрЃЋрЃћрЃџрЃў рЃцрЃЮрЃбрЃЮ. рЃЌрЃБ рЃБрЃљрЃарЃА рЃњрЃћрЃбрЃДрЃЋрЃўрЃА, рЃАрЃЋрЃљрЃЏрЃА рЃАрЃљрЃЏрЃ»рЃћрЃа. рЃЌрЃБ рЃЊрЃљрЃњрЃЌрЃљрЃюрЃ«рЃЏрЃЊрЃћрЃЉрЃљ, рЃАрЃЋрЃљрЃЏ рЃерЃћрЃю.",
        "рЃЊрЃљрЃБрЃарЃћрЃЎрЃћ рЃЏрЃерЃЮрЃЉрЃћрЃџрЃА рЃљрЃю рЃюрЃљрЃЌрЃћрЃАрЃљрЃЋрЃА рЃЎрЃўрЃЌрЃ«рЃЋрЃўрЃЌ: рЃарЃљ рЃдрЃўрЃарЃА рЃЕрЃўрЃюрЃБрЃарЃў рЃЏрЃљрЃАрЃљрЃЪрЃў рЃЊрЃљ рЃАрЃљрЃЊ рЃљрЃЎрЃћрЃЌрЃћрЃЉрЃћрЃю рЃДрЃЋрЃћрЃџрЃљрЃќрЃћ рЃЎрЃљрЃарЃњрЃљрЃЊ?.рЃБрЃљрЃарЃўрЃА рЃерЃћрЃЏрЃЌрЃ«рЃЋрЃћрЃЋрЃљрЃерЃў рЃАрЃЋрЃљрЃЏ рЃЮрЃарЃ»рЃћрЃа.",
        "рЃерЃћрЃерЃЊрЃћрЃЉрЃљ рЃДрЃЋрЃћрЃџрЃљ, рЃарЃЮрЃфрЃљ рЃ«рЃћрЃџрЃА рЃЏрЃљрЃдрЃџрЃљ рЃљрЃгрЃћрЃЋ. рЃЌрЃљрЃЏрЃљрЃерЃўрЃА рЃњрЃљрЃюрЃЏрЃљрЃЋрЃџрЃЮрЃЉрЃљрЃерЃў рЃерЃћрЃњрЃўрЃФрЃџрЃўрЃљ рЃАрЃљрЃЏрЃ»рЃћрЃа рЃњрЃљрЃљрЃерЃћрЃерЃЮ рЃЏрЃЮрЃЌрЃљрЃЏрЃљрЃерЃћрЃћрЃЉрЃў. рЃЋрЃўрЃюрЃф рЃњрЃљрЃерЃћрЃерЃћрЃЉрЃљрЃА рЃЋрЃћрЃа рЃЏрЃЮрЃљрЃАрЃгрЃарЃћрЃЉрЃА рЃАрЃЋрЃљрЃЏрЃА.",
        "рЃЊрЃљрЃБрЃарЃћрЃЎрЃћ рЃарЃЮрЃЏрЃћрЃџрЃўрЃЏрЃћ рЃарЃћрЃАрЃбрЃЮрЃарЃљрЃюрЃА рЃ«рЃЏрЃљрЃЏрЃљрЃдрЃљрЃџ рЃарЃћрЃЪрЃўрЃЏрЃќрЃћ. рЃЊрЃљрЃ»рЃљрЃЋрЃерЃюрЃћ рЃЏрЃљрЃњрЃўрЃЊрЃљ 9 рЃЌрЃћрЃцрЃерЃўрЃЌ рЃЊрЃљ 1 рЃАрЃЎрЃљрЃЏрЃўрЃЌ рЃфрЃ«рЃарЃљрЃЌрЃљрЃЋрЃўрЃљрЃюрЃў рЃЊрЃћрЃЋрЃўрЃАрЃЌрЃЋрЃўрЃА. рЃБрЃарЃўрЃА рЃерЃћрЃЏрЃЌрЃ«рЃЋрЃћрЃЋрЃљрЃерЃў рЃАрЃЋрЃљрЃЏ рЃЮрЃЌрЃ«рЃ»рЃћрЃа.",
        "рЃњрЃљрЃБрЃњрЃќрЃљрЃЋрЃюрЃћ рЃерЃћрЃю рЃДрЃЮрЃцрЃўрЃџ рЃерЃћрЃДрЃЋрЃљрЃарЃћрЃЉрЃБрЃџрЃА/рЃърЃљрЃарЃбрЃюрЃўрЃЮрЃарЃА: *­ЪќЋ* рЃљрЃю рЃЊрЃљрЃџрЃўрЃћ рЃЮрЃарЃ»рЃћрЃа.",
        "рЃЋрЃўрЃАрЃЌрЃЋрЃўрЃАрЃљрЃф рЃърЃЮрЃарЃюрЃЮрЃА рЃДрЃБрЃарЃћрЃЉрЃўрЃАрЃљрЃА рЃгрЃљрЃБрЃАрЃгрЃЋрЃарЃўрЃљрЃЌ, рЃАрЃЋрЃљрЃЏрЃА.",
        "рЃљрЃўрЃарЃЕрЃўрЃћрЃЌ рЃЏрЃЮрЃЌрЃљрЃЏрЃљрЃерЃћ, рЃљрЃўрЃДрЃЋрЃћрЃюрЃћрЃЌ рЃ«рЃћрЃџрЃерЃў рЃЊрЃљ рЃўрЃАрЃћ рЃЊрЃљрЃљрЃџрЃћрЃЋрЃўрЃюрЃћрЃЌ.",
        "рЃћрЃарЃЌрЃў рЃгрЃБрЃЌрЃўрЃА рЃњрЃљрЃюрЃЏрЃљрЃЋрЃџрЃЮрЃЉрЃљрЃерЃў рЃДрЃЋрЃћрЃџрЃљ рЃЏрЃЮрЃЌрЃљрЃЏрЃљрЃерЃћ рЃўрЃЏрЃћрЃЮрЃарЃћрЃЉрЃА рЃДрЃЋрЃћрЃџрЃљрЃцрЃћрЃарЃА рЃарЃљрЃАрЃљрЃф рЃерЃћрЃю рЃљрЃЎрЃћрЃЌрЃћрЃЉ. рЃЋрЃўрЃюрЃф рЃДрЃЋрЃћрЃџрЃљрЃќрЃћ рЃфрЃБрЃЊрЃљрЃЊ рЃЏрЃЮрЃњрЃЉрЃљрЃФрЃљрЃЋрЃА рЃАрЃЋрЃљрЃЏрЃА рЃЮрЃарЃ»рЃћрЃа.",
        "рЃЋрЃўрЃюрЃф рЃЉрЃљрЃЋрЃерЃЮрЃЉрЃљрЃерЃў рЃцрЃарЃЕрЃ«рЃўрЃџрЃћрЃЉрЃА рЃўрЃЎрЃЋрЃюрЃћрЃбрЃЊрЃљ, рЃАрЃЋрЃљрЃЏрЃА.",
        "рЃЏрЃљрЃарЃфрЃ«рЃћрЃюрЃљ рЃЌрЃЋрЃљрЃџрЃўрЃЌ рЃерЃћрЃ«рЃћрЃЊрЃћ рЃерЃћрЃюрЃњрЃљрЃю рЃЏрЃљрЃарЃфрЃ«рЃюрЃўрЃЋ рЃЏрЃ»рЃЊрЃЮрЃЏрЃА. рЃЏрЃљрЃарЃ»рЃЋрЃћрЃюрЃљ рЃЌрЃЋрЃљрЃџрЃўрЃЌ рЃерЃћрЃ«рЃћрЃЊрЃћ рЃерЃћрЃюрЃњрЃљрЃю рЃЏрЃљрЃарЃ»рЃЋрЃюрЃўрЃЋ рЃЏрЃ»рЃЊрЃЮрЃЏрЃА. рЃЌрЃБ рЃЋрЃћрЃа рЃерЃћрЃљрЃАрЃарЃБрЃџрЃћрЃЉ, рЃАрЃЋрЃљрЃЏ рЃЮрЃарЃ»рЃћрЃа. рЃЌрЃБ рЃерЃћрЃљрЃАрЃарЃБрЃџрЃћрЃЉ, рЃАрЃљрЃдрЃЮрЃџ рЃерЃћрЃю!",
        "рЃбрЃљрЃерЃўрЃА рЃЊрЃљрЃЎрЃЋрЃарЃўрЃЌ рЃљрЃљрЃгрЃДрЃћ рЃарЃљрЃўрЃЏрЃћ рЃЏрЃћрЃџрЃЮрЃЊрЃўрЃљ. рЃДрЃЋрЃћрЃџрЃљ рЃЏрЃЮрЃЌрЃљрЃЏрЃљрЃерЃћрЃЏ рЃќрЃБрЃАрЃбрЃљрЃЊ рЃБрЃюрЃЊрЃљ рЃњрЃљрЃўрЃЏрЃћрЃЮрЃарЃЮрЃА рЃўрЃњрЃў. рЃЋрЃўрЃАрЃљрЃф рЃерЃћрЃћрЃерЃџрЃћрЃЉрЃљ, рЃАрЃЋрЃљрЃЏрЃА рЃЮрЃарЃ»рЃћрЃа. рЃЌрЃБ рЃДрЃЋрЃћрЃџрЃљ рЃњрЃљрЃўрЃЏрЃћрЃЮрЃарЃћрЃЉрЃА рЃЮрЃарЃ»рЃћрЃа рЃАрЃЋрЃљрЃЏ рЃерЃћрЃю.",
        "рЃ«рЃБрЃЌрЃ»рЃћрЃа рЃАрЃгрЃарЃљрЃцрЃљрЃЊ рЃЌрЃЦрЃЋрЃў: *рЃЕрЃўрЃбрЃў, рЃЕрЃ«рЃўрЃЎрЃЋрЃў, рЃфрЃўрЃДрЃЋрЃў*.рЃЌрЃБ рЃерЃћрЃњрЃћрЃерЃџрЃћрЃЉрЃљ рЃАрЃЋрЃљрЃЏ рЃЮрЃарЃ»рЃћрЃа, рЃЌрЃБ рЃљрЃарЃљ, рЃљрЃАрЃЏрЃћрЃЋ рЃАрЃ«рЃЋрЃљ рЃЏрЃЮрЃЌрЃљрЃЏрЃљрЃерЃћрЃА.",
        "рЃЕрЃљрЃўрЃњрЃБрЃЉрЃћ рЃърЃўрЃарЃерЃў рЃљрЃџрЃЎрЃЮрЃ░рЃЮрЃџрЃў 30 рЃгрЃљрЃЏрЃўрЃЌ. рЃЏрЃЮрЃЌрЃљрЃЏрЃљрЃерЃћрЃћрЃЉрЃў рЃерЃћрЃћрЃфрЃЊрЃћрЃЉрЃўрЃљрЃю рЃерЃћрЃюрЃА рЃњрЃљрЃфрЃўрЃюрЃћрЃЉрЃљрЃА. рЃЌрЃБ рЃњрЃљрЃњрЃљрЃфрЃўрЃюрЃћрЃЉрЃћрЃю, рЃАрЃЋрЃљрЃЏ рЃЮрЃарЃ»рЃћрЃа. рЃЌрЃБ рЃЋрЃћрЃарЃљ рЃАрЃЋрЃљрЃЏрЃА рЃДрЃЋрЃћрЃџрЃљ рЃЊрЃљрЃюрЃљрЃарЃЕрЃћрЃюрЃў.",
        "рЃерЃћрЃюрЃњрЃљрЃю рЃЏрЃљрЃарЃ»рЃЋрЃюрЃўрЃЋ рЃЏрЃ»рЃЊрЃЮрЃЏрЃў рЃерЃћрЃюрЃў рЃЉрЃЮрЃАрЃўрЃљ. рЃерЃћрЃюрЃА рЃерЃћрЃЏрЃЊрЃћрЃњ рЃАрЃЋрЃџрЃљрЃЏрЃЊрЃћ рЃљрЃАрЃарЃБрЃџрЃћрЃЉ рЃДрЃЋрЃћрЃџрЃљрЃцрЃћрЃарЃА, рЃарЃљрЃАрЃљрЃф рЃЊрЃљрЃњрЃљрЃЋрЃљрЃџрЃћрЃЉрЃА. рЃгрЃўрЃюрЃљрЃљрЃдрЃЏрЃЊрЃћрЃњ рЃерЃћрЃЏрЃЌрЃ«рЃЋрЃћрЃЋрЃљрЃерЃў рЃАрЃЋрЃљрЃЏ рЃЮрЃарЃ»рЃћрЃа.",
        "рЃюрЃћрЃЉрЃљ рЃЏрЃўрЃћрЃфрЃў рЃарЃЮрЃЏрЃћрЃџрЃўрЃЏрЃћ рЃЏрЃЮрЃЌрЃљрЃЏрЃљрЃерЃћрЃА 20 рЃгрЃљрЃЏрЃўрЃА рЃњрЃљрЃюрЃЏрЃљрЃЋрЃџрЃЮрЃЉрЃљрЃерЃў рЃ«рЃћрЃџрЃў рЃњрЃўрЃарЃбрЃДрЃљрЃА рЃАрЃљрЃ»рЃЊрЃЮрЃЏрЃќрЃћ. рЃЌрЃљрЃю рЃўрЃФрЃљрЃ«рЃћ: *рЃБрЃцрЃарЃЮ рЃЏрЃљрЃњрЃарЃљрЃЊ*.рЃБрЃљрЃарЃўрЃА рЃерЃћрЃЏрЃЌрЃ«рЃЋрЃћрЃЋрЃљрЃерЃў рЃАрЃЋрЃљрЃЏ рЃАрЃљрЃЏрЃ»рЃћрЃа.",
        "рЃљрЃўрЃарЃЕрЃўрЃћрЃЌ рЃЏрЃЮрЃЌрЃљрЃЏрЃљрЃерЃћ рЃЕрЃљрЃБрЃЊрЃћрЃЌ рЃърЃўрЃарЃерЃў рЃФрЃљрЃЉрЃарЃў рЃЊрЃљ рЃљрЃАрЃћ рЃЊрЃљрЃљрЃџрЃћрЃЋрЃўрЃюрЃћрЃЌ. рЃБрЃљрЃарЃўрЃА рЃерЃћрЃЏрЃЌрЃ«рЃЋрЃћрЃЋрЃљрЃерЃў рЃАрЃЋрЃљрЃЏрЃА рЃЮрЃЌрЃ«рЃ»рЃћрЃа.",
        "рЃЕрЃљрЃўрЃЊрЃћ рЃЌрЃўрЃЌрЃЮ рЃДрЃўрЃюрЃБрЃџрЃў рЃЮрЃарЃўрЃЋрЃћ рЃўрЃдрЃџрЃўрЃљрЃерЃў рЃЊрЃљ рЃњрЃћрЃЊрЃЮрЃА, рЃАрЃљрЃюрЃљрЃЏ рЃљрЃа рЃЊрЃљрЃЊрЃюрЃћрЃЉрЃљ. рЃгрЃўрЃюрЃљрЃљрЃдрЃЏрЃЊрЃћрЃњ рЃерЃћрЃЏрЃЌрЃ«рЃЋрЃћрЃЋрЃљрЃерЃў рЃАрЃЋрЃљрЃЏ рЃЮрЃЌрЃ«рЃ»рЃћрЃа.",
        "рЃДрЃЋрЃћрЃџрЃљ рЃЋрЃўрЃАрЃљрЃф рЃЌрЃљрЃЋрЃўрЃАрЃў рЃЏрЃљрЃАрЃгрЃљрЃЋрЃџрЃћрЃЉрЃћрЃџрЃў/рЃџрЃўрЃЦрЃбрЃЮрЃарЃў рЃЏрЃЮрЃгрЃЮрЃюрЃћрЃЉрЃўрЃљ, рЃАрЃЋрЃљрЃЏрЃА.",
        "рЃњрЃљрЃўрЃ«рЃљрЃЊрЃћ рЃцрЃћрЃ«рЃАрЃљрЃфрЃЏрЃћрЃџрЃў рЃЕрЃљрЃЊрЃћ рЃЏрЃљрЃАрЃерЃў рЃЉрЃЮрЃЌрЃџрЃў рЃЊрЃљ рЃљрЃАрЃћ рЃЊрЃљрЃџрЃўрЃћ. рЃгрЃўрЃюрЃљрЃљрЃдрЃЏрЃЊрЃћрЃњ рЃерЃћрЃЏрЃЌрЃ«рЃЋрЃћрЃЋрЃљрЃерЃў рЃАрЃЋрЃљрЃЏ рЃАрЃљрЃЏрЃ»рЃћрЃа.",
        "рЃ«рЃарЃўрЃЌ рЃЌрЃљрЃЋрЃћрЃЉрЃА. рЃАрЃљрЃЏ рЃЌрЃЋрЃџрЃљрЃќрЃћ рЃўрЃ«рЃћрЃЊрЃћрЃЉрЃўрЃЌ рЃЊрЃљ рЃўрЃарЃЕрЃћрЃЋрЃЌ рЃЏрЃЮрЃЌрЃљрЃЏрЃљрЃерЃћрЃА, рЃарЃЮрЃЏрЃћрЃџрЃАрЃљрЃф рЃерЃћрЃ«рЃћрЃЊрЃљрЃЋрЃЌ. рЃДрЃЋрЃћрЃџрЃљ рЃгрЃДрЃЋрЃўрЃџрЃў рЃарЃЮрЃЏрЃћрЃџрЃўрЃф рЃћрЃарЃЌрЃЏрЃљрЃюрЃћрЃЌрЃА рЃерЃћрЃ«рЃћрЃЊрЃљрЃЋрЃА, рЃАрЃЋрЃљрЃЏрЃА.",
        "рЃерЃћрЃюрЃА рЃърЃўрЃарЃЊрЃљрЃърЃўрЃа рЃЏрЃ»рЃЊрЃЮрЃЏрЃў рЃЏрЃЮрЃЌрЃљрЃЏрЃљрЃерЃћ рЃћрЃюрЃўрЃЌ рЃБрЃюрЃЊрЃљ рЃерЃћрЃћрЃ«рЃЮрЃА рЃАрЃљрЃЎрЃБрЃЌрЃљрЃа рЃфрЃ«рЃЋрЃўрЃарЃА. рЃЌрЃБ рЃЋрЃћрЃа рЃЏрЃЮрЃљрЃ«рЃћрЃарЃ«рЃћрЃЉрЃА, рЃАрЃЋрЃљрЃЏрЃА рЃЮрЃарЃ»рЃћрЃа. рЃЌрЃБ рЃЏрЃЮрЃљрЃ«рЃћрЃарЃ«рЃћрЃЉрЃА, рЃЮрЃарЃ»рЃћрЃа рЃАрЃЋрЃљрЃЏрЃА рЃДрЃЋрЃћрЃџрЃљ рЃЊрЃљрЃюрЃљрЃарЃЕрЃћрЃюрЃў.",
        "рЃЋрЃўрЃАрЃљрЃф рЃљрЃ«рЃџрЃљ рЃЏрЃЮрЃЉрЃўрЃџрЃБрЃарЃў рЃбрЃћрЃџрЃћрЃцрЃЮрЃюрЃў рЃГрЃўрЃарЃљрЃЋрЃА рЃ«рЃћрЃџрЃерЃў, рЃАрЃЋрЃљрЃЏрЃА.",
        "рЃЊрЃљрЃ«рЃБрЃГрЃћ рЃЌрЃЋрЃљрЃџрЃћрЃЉрЃў. рЃћрЃарЃЌ-рЃћрЃарЃЌрЃў рЃЏрЃЮрЃЌрЃљрЃЏрЃљрЃерЃћ рЃЏрЃЮрЃЋрЃљ рЃерЃћрЃюрЃЌрЃљрЃю рЃерЃћрЃю рЃЏрЃљрЃАрЃќрЃћ рЃерЃћрЃ«рЃћрЃЉрЃўрЃЌ рЃБрЃюрЃЊрЃљ рЃЏрЃўрЃ«рЃЋрЃЊрЃћ рЃЋрЃўрЃюрЃљрЃљ. рЃЌрЃБ рЃњрЃљрЃЏрЃЮрЃўрЃфрЃюрЃЮрЃЉ, рЃАрЃЋрЃљрЃЏрЃА рЃДрЃЋрЃћрЃџрЃљ. рЃЌрЃБ рЃЋрЃћрЃарЃљ, рЃАрЃЋрЃљрЃЏ рЃЮрЃарЃ»рЃћрЃа.",
        "рЃњрЃљрЃБрЃфрЃЋрЃљрЃџрЃћ рЃќрЃћрЃЊрЃљ, рЃњрЃЋрЃћрЃарЃЊрЃўрЃЌ рЃЏрЃ»рЃЊрЃЮрЃЏрЃА. рЃБрЃљрЃарЃўрЃА рЃерЃћрЃЏрЃЌрЃ«рЃЋрЃћрЃЋрЃљрЃерЃў рЃЮрЃарЃўрЃЋрЃћ рЃАрЃЋрЃљрЃЏрЃЌ рЃАрЃљрЃЏрЃ»рЃћрЃа. рЃЌрЃБ рЃњрЃљрЃњрЃўрЃфрЃЋрЃџрЃўрЃА, рЃўрЃарЃЕрЃћрЃЋ рЃЋрЃўрЃю рЃЊрЃљ рЃарЃљрЃЏрЃЊрЃћрЃюрЃ»рЃћрЃа рЃЊрЃљрЃџрЃўрЃЮрЃА.",
        "рЃЋрЃўрЃАрЃљрЃф рЃ«рЃћрЃџрЃерЃў рЃАрЃљрЃАрЃЏрЃћрЃџрЃў рЃБрЃГрЃўрЃарЃљрЃЋрЃА, рЃАрЃЋрЃљрЃЏрЃА.",
        "рЃДрЃЋрЃћрЃџрЃљ рЃАрЃЋрЃљрЃЏрЃА рЃ«рЃћрЃџрЃћрЃЉрЃўрЃА рЃЊрЃљрЃБрЃ«рЃЏрЃљрЃарЃћрЃЉрЃџрЃљрЃЊ рЃЋрЃўрЃАрЃљрЃф рЃљрЃа рЃњрЃљрЃЏрЃЮрЃБрЃЋрЃљ, рЃАрЃЋрЃљрЃЏрЃА рЃЮрЃарЃ»рЃћрЃа",
        "рЃерЃћрЃюрЃў рЃбрЃћрЃџрЃћрЃцрЃЮрЃюрЃўрЃЊрЃљрЃю рЃарЃћрЃЎрЃљрЃЋрЃЌ рЃарЃЮрЃЏрЃћрЃџрЃўрЃЏрЃћ рЃЎрЃЮрЃюрЃбрЃљрЃЦрЃбрЃЌрЃљрЃю рЃ«рЃЏрЃљрЃЏрЃљрЃдрЃљрЃџрЃў рЃарЃћрЃЪрЃўрЃЏрЃўрЃЌ. рЃарЃЮрЃЊрЃћрЃАрЃљрЃф рЃњрЃўрЃърЃљрЃАрЃБрЃ«рЃћрЃЉрЃЌ рЃДрЃЋрЃћрЃџрЃљ рЃњрЃљрЃЏрЃЮрЃАрЃфрЃћрЃЏрЃЌ рЃўрЃюрЃбрЃўрЃЏрЃБрЃа рЃ«рЃЏрЃћрЃЉрЃА. рЃгрЃўрЃюрЃљрЃљрЃдрЃЏрЃЊрЃћрЃњ рЃерЃћрЃЏрЃЌрЃ«рЃЋрЃћрЃЋрЃљрЃерЃў рЃДрЃЋрЃћрЃџрЃљ рЃАрЃЋрЃљрЃЏрЃА рЃЮрЃарЃ»рЃћрЃа.",
        "рЃЕрЃљрЃЏрЃЮрЃЌрЃЋрЃљрЃџрЃћ рЃАрЃљрЃЏрЃў рЃцрЃљрЃЦрЃбрЃў рЃўрЃЏ рЃЦрЃБрЃЕрЃўрЃА рЃАрЃљрЃ«рЃћрЃџрЃгрЃЮрЃЊрЃћрЃЉрЃўрЃА, рЃљрЃю рЃўрЃЏ рЃљрЃЊрЃљрЃЏрЃўрЃљрЃюрЃўрЃА рЃерЃћрЃАрЃљрЃ«рЃћрЃЉ, рЃарЃЮрЃЏрЃџрЃўрЃА рЃАрЃљрЃ«рЃћрЃџрЃЮрЃЉрЃўрЃА рЃЦрЃБрЃЕрЃљрЃќрЃћрЃф рЃфрЃ«рЃЮрЃЋрЃарЃЮрЃЉ. рЃЌрЃБ рЃЕрЃљрЃЏрЃЮрЃЌрЃЋрЃџрЃў рЃљрЃАрЃЏрЃћрЃЋ рЃАрЃ«рЃЋрЃљрЃА, рЃЌрЃБ рЃЋрЃћрЃарЃљ, рЃАрЃЋрЃљрЃЏ рЃАрЃљрЃЏрЃ»рЃћрЃа.",
        "рЃЏрЃўрЃгрЃћрЃарЃћ: *рЃ░рЃћрЃарЃърЃћрЃАрЃў рЃЏрЃљрЃЦрЃЋрЃА* рЃЏрЃљрЃА, рЃЋрЃўрЃАрЃљрЃф рЃЉрЃЮрЃџрЃЮрЃА рЃљрЃЎрЃЮрЃфрЃћ.рЃЌрЃљрЃЏрЃљрЃерЃўрЃА рЃЉрЃЮрЃџрЃЮрЃЏрЃЊрЃћ рЃљрЃа рЃњрЃљрЃАрЃфрЃћ рЃърЃљрЃАрЃБрЃ«рЃў рЃљрЃю рЃЊрЃљрЃџрЃўрЃћ рЃ«рЃБрЃЌрЃ»рЃћрЃа.",
        "рЃЏрЃЮрЃДрЃћрЃЋрЃў рЃерЃћрЃюрЃў рЃърЃўрЃарЃЋрЃћрЃџрЃў рЃАрЃћрЃЦрЃАрЃўрЃА рЃўрЃАрЃбрЃЮрЃарЃўрЃљ рЃљрЃю рЃЊрЃљрЃџрЃўрЃћ рЃЮрЃарЃ»рЃћрЃа. рЃЌрЃБ рЃАрЃћрЃЦрЃАрЃў рЃљрЃарЃљрЃАрЃЮрЃЊрЃћрЃА рЃњрЃЦрЃЮрЃюрЃўрЃљ, рЃАрЃЋрЃљрЃЏ рЃАрЃљрЃЏрЃ»рЃћрЃа.",
        "рЃЋрЃўрЃюрЃф рЃерЃћрЃюрЃќрЃћ рЃЏрЃљрЃдрЃљрЃџрЃўрЃљ, рЃАрЃЋрЃљрЃЏрЃА рЃЮрЃарЃ»рЃћрЃа.",
        "рЃЋрЃўрЃАрЃљрЃф рЃарЃЮрЃЏрЃћрЃџрЃўрЃЏрЃћ рЃЏрЃЮрЃЌрЃљрЃЏрЃљрЃерЃћрЃАрЃЌрЃљрЃю, рЃљрЃю рЃЏрЃЮрЃЌрЃљрЃЏрЃљрЃерЃўрЃА рЃЏрЃћрЃњрЃЮрЃЉрЃљрЃарЃЌрЃљрЃю рЃБрЃцрЃџрЃўрЃарЃбрЃљрЃЋрЃўрЃљ, рЃАрЃЋрЃљрЃЏрЃА.",
        "рЃЊрЃљрЃўрЃГрЃўрЃарЃћ рЃАрЃљрЃАрЃЏрЃўрЃАрЃў рЃЏрЃБрЃџрЃћрЃЉрЃўрЃЌ рЃЊрЃљ рЃАрЃфрЃљрЃЊрЃћ рЃљрЃАрЃћ рЃЊрЃљрЃџрЃћрЃЋрЃљ. рЃЌрЃБ рЃљрЃа рЃњрЃљрЃЏрЃЮрЃњрЃўрЃЋрЃљ, рЃАрЃЋрЃљрЃЏ рЃЮрЃарЃ»рЃћрЃа.", ]
        
        
